import React, { useState } from "react";
import "./BookingForm.css";
import Button from "../../components/Button/Button";

const BookingForm = ({
    onFormSubmit,
    isFormSubmitted,
    availableTimes,
    dispatchOnDateChange
}) => {
    const defaultTime = availableTimes[0];

    const [formValues, setFormValues] = useState({
        date: "",
        time: defaultTime,
        people: "",
        occasion: "",

    });

    const handleInputChange = (e) => {
        if(e.target.name === 'date')
        {
            dispatchOnDateChange(e.target.value);
        }

        setFormValues({
            ...formValues,
            [e.target.name]: e.target.value,
        });
    };

    const minGuest = 1;
    const maxGuest = 10;

    return (
        <form onSubmit={(e) => onFormSubmit(e, formValues)}>
            <div className="reservation-container">
                <label htmlFor="date" className="conatiner-item-title">
                    Date
                </label>
                <input 
                    type="date"
                    id="date"
                    name="date"
                    onChange={handleInputChange}
                    className={isFormSubmitted && !formValues.date ? "error" : ""}
                />
            </div>
            <div className="reservation-container">
                <label htmlFor="time" className="conatiner-item-title">
                    Time
                </label>
                <select 
                id="time"
                name="time"
                value={formValues.time}
                onChange={handleInputChange}
                className={isFormSubmitted && !formValues.time ? "error" : ""}
                >
                    {availableTimes.map(time => 
                        <option key={time}>
                            {time}
                        </option>
                    )}
                </select>
            </div>
            <div className="reservation-container">
                <label htmlFor="people" className="conatiner-item-title">
                    Number of people
                </label>
                <input 
                type="number"
                id="people"
                name="people"
                min={minGuest}
                max={maxGuest}
                onChange={handleInputChange}
                className={isFormSubmitted && !formValues.people ? "error" : ""}
                />
            </div>
            <div className="reservation-container">
                <label htmlFor="occasion" className="conatiner-item-title">
                    Occasion
                </label>
                <select 
                id="occasion"
                name="occasion"
                onChange={handleInputChange}
                className={isFormSubmitted && !formValues.occasion ? "error" : ""}
                >
                    <option value="birthday">Birthday</option>
                    <option value="anniversary">Anniversary</option>
                    <option value="business">Business</option>
                    <option value="other">Other</option>
                </select>
            </div>
            <div className="reservation-button">
                <Button title="Book a table" type="submit"/>
            </div>
        </form>
    );
};

export default BookingForm;
